\set nbranches :scale
\set ntellers 10 * :scale
\set naccounts 100000 * :scale
\setrandom aid 1 :naccounts
\setrandom bid 1 :nbranches
\setrandom tid 1 :ntellers
\setrandom delta -5000 5000
BEGIN;

-- pgp_pub_encrypt(delta::text, $$\$$::bytea)
-- pgp_pub_decrypt(delta::text, $$\$$::bytea,''::text)
-- UPDATE pgbench_accounts SET abalance = pgp_pub_encrypt((convert_from(pgp_pub_decrypt(abalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int + :delta )::text, $$\$$::bytea ) WHERE aid = :aid;
-- SELECT convert_from(pgp_pub_decrypt(abalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  FROM pgbench_accounts WHERE aid = :aid;
-- UPDATE pgbench_tellers SET tbalance = pgp_pub_encrypt((convert_from(pgp_pub_decrypt(tbalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  + :delta )::text,$$\$$::bytea ) WHERE tid = :tid;
-- UPDATE pgbench_branches SET bbalance = pgp_pub_encrypt((convert_from(pgp_pub_decrypt(bbalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  + :delta )::text,$$\$$::bytea ) WHERE bid = :bid;
-- INSERT INTO pgbench_history (tid, bid, aid, delta, mtime) VALUES (:tid, :bid, :aid, :delta, CURRENT_TIMESTAMP);
UPDATE pgbench_accounts SET abalance = (convert_from(pgp_pub_decrypt(abalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int + :delta )::text::bytea WHERE aid = :aid;
SELECT convert_from(pgp_pub_decrypt(abalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  FROM pgbench_accounts WHERE aid = :aid;
UPDATE pgbench_tellers SET tbalance = (convert_from(pgp_pub_decrypt(tbalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  + :delta )::text::bytea WHERE tid = :tid;
UPDATE pgbench_branches SET bbalance = (convert_from(pgp_pub_decrypt(bbalance::text::bytea, $$\$$::bytea,''::text)::bytea,'SQL_ASCII'::name)::int  + :delta )::text::bytea  WHERE bid = :bid;
INSERT INTO pgbench_history (tid, bid, aid, delta, mtime) VALUES (:tid, :bid, :aid, (:delta)::text::bytea, (CURRENT_TIMESTAMP)::text::bytea );

END;
